mapper.select:
SELECT NVL(B.ITEM_NUM,0) AS ITEM_NUM, NVL(B.AGENT_ID, '' ) AS USER_ID, NVL(B.ITEM_NAME, '') AS ITEM_NAME, NVL(B.ITEM_ADDRESS, '') AS ITEM_ADDRESS, NVL(C.ITEM_AREA, 0.0) AS ITEM_AREA, NVL(B.ITEM_ADDRESS_DETAIL, '') AS ITEM_ADDRESS_DETAIL, NVL(B.ITEM_PTYPE, '') AS ITEM_PTYPE, NVL(B.ITEM_ITYPE, '') AS ITEM_ITYPE, NVL(B.ITEM_STATUS, 'N') AS ITEM_STATUS, NVL(B.RECOMMEND_ITEM, 'N') AS RECOMMEND_ITEM, B.ITEM_COUNT,
I. LIKE_ITEM,
NVL(C.ITEM_INTRO, '') AS ITEM_INTRO, NVL(C.ITEM_DEPOSIT, 0) AS ITEM_DEPOSIT, NVL(C.ITEM_MONTH_PRICE, 0) AS ITEM_MONTH_PRICE, NVL(C.ITEM_BUILD_DATE, '') AS ITEM_BUILD_DATE, NVL(A.FILE_NAME, '') AS FILE_NAME
FROM ZIP_ITEM B, (SELECT ITEM_NUM, FILE_NAME FROM ZIP_ITEM_FILE WHERE FILE_IDX = 1 ORDER BY FILE_NUM ASC) A, ZIP_ITEM_DETAIL C
,(SELECT DISTINCT CASE WHEN USER_ID = ${userId} THEN 'Y' ELSE 'N' END AS LIKE_ITEM FROM ZIP_INTEREST_ITEM WHERE ITEM_NUM =#{itemNum}) I
WHERE B.ITEM_ITYPE = #{searchType} AND B.ITEM_NUM = C.ITEM_NUM(+) AND B.ITEM_NUM = A.ITEM_NUM(+) AND B.ITEM_STATUS = 'Y'
AND (
B.ITEM_ADDRESS LIKE '%' || #{searchAdd} || '%'
)
AND B.ITEM_NUM = #{itemNum}
AND C.ITEM_AREA >= #{searchSize}
AND B.ITEM_PTYPE = #{pType}
AND C.ITEM_DEPOSIT BETWEEN #{priceStart} AND #{priceEnd}
AND I.USER_ID = #{userId}

SELECT D.ITEM_INTRO, D.ITEM_INTRO_DETAIL, D.ITEM_DEPOSIT, D.ITEM_MAINTAIN_PRICE, D.ITEM_MONTH_PRICE, D.ITEM_AREA, D.ITEM_PARKING, NVL(TO_CHAR(TO_DATE(D.ITEM_BUILD_DATE, 'YYYYMMDD'),'YYYY.MM.DD'),'') ITEM_BUILD_DATE, D.ITEM_ELEVATOR, D.ITEM_PET, D.ITEM_DIRECTION, D.ITEM_KIND, D.ITEM_FLOOR, D.ITEM_MOVE_AVBL, NVL(TO_CHAR(TO_DATE(D.ITEM_MOVE_DATE, 'YYYYMMDD'),'YYYY.MM.DD'),'') ITEM_MOVE_DATE, A.ITEM_NAME, A.AGENT_ID, A.ITEM_ADDRESS, A.ITEM_ADDRESS_DETAIL, A.ITEM_PTYPE, A.ITEM_STATUS, TRUNC(SYSDATE - TO_DATE(A.REG_DATE, 'YY/MM/DD')) AS REG_DATE, A.RECOMMEND_ITEM, A.ITEM_COUNT,
I. LIKE_ITEM,
U.AGENT_NAME, U.AGENT_INTRO, U.AGENT_RIGHT FROM ZIP_ITEM_DETAIL D, ZIP_ITEM A, ZIP_AGENT U
,(SELECT DISTINCT CASE WHEN USER_ID = ${userId} THEN 'Y' ELSE 'N' END AS LIKE_ITEM FROM ZIP_INTEREST_ITEM WHERE ITEM_NUM =#{itemNum}) I)
WHERE 1=1 AND D.ITEM_NUM = A.ITEM_NUM AND A.AGENT_ID = U.AGENT_ID(+) AND D.ITEM_NUM = #{itemNum}
AND I.USER_ID = ${userId}

mapper.insert:
INSERT INTO ZIP_REPORT (
            REPORT_NUM,
            USER_ID,
            REPORT_TYPE,
            ITEM_NUM,
            REPORT_CONTENT,
            REG_DATE,
            STATUS
) VALUES (
        (SELECT MAX(REPORT_NUM)+1 FROM ZIP_REPORT)
        ,#{userId}
        ,#{reportType}
        ,#{itemNum}
        ,#{reportContent}
        ,SYSDATE
        ,'P')
